		 /// <summary>
        /// 校验Excel数据,这个方法一般用于重写校验逻辑
        /// </summary>
        public virtual bool CheckImportData(string fileName, List<Flow_StepRuleModel> list,ref ValidationErrors errors )
        {
          
            var targetFile = new FileInfo(fileName);

            if (!targetFile.Exists)
            {

                errors.Add("导入的数据文件不存在");
                return false;
            }

            var excelFile = new ExcelQueryFactory(fileName);

            //对应列头
			 				 excelFile.AddMapping<Flow_StepRuleModel>(x => x.StepId, "StepId");
				 excelFile.AddMapping<Flow_StepRuleModel>(x => x.AttrId, "AttrId");
				 excelFile.AddMapping<Flow_StepRuleModel>(x => x.Operator, "Operator");
				 excelFile.AddMapping<Flow_StepRuleModel>(x => x.Result, "Result");
				 excelFile.AddMapping<Flow_StepRuleModel>(x => x.NextStep, "NextStep");
 
            //SheetName
            var excelContent = excelFile.Worksheet<Flow_StepRuleModel>(0);
            int rowIndex = 1;
            //检查数据正确性
            foreach (var row in excelContent)
            {
                var errorMessage = new StringBuilder();
                var entity = new Flow_StepRuleModel();
						 				  entity.Id = row.Id;
				  entity.StepId = row.StepId;
				  entity.AttrId = row.AttrId;
				  entity.Operator = row.Operator;
				  entity.Result = row.Result;
				  entity.NextStep = row.NextStep;
 
                //=============================================================================
                if (errorMessage.Length > 0)
                {
                    errors.Add(string.Format(
                        "第 {0} 列发现错误：{1}{2}",
                        rowIndex,
                        errorMessage,
                        "<br/>"));
                }
                list.Add(entity);
                rowIndex += 1;
            }
            if (errors.Count > 0)
            {
                return false;
            }
            return true;
        }

