		 /// <summary>
        /// 校验Excel数据,这个方法一般用于重写校验逻辑
        /// </summary>
        public virtual bool CheckImportData(string fileName, List<MIS_Article_AlbumsModel> list,ref ValidationErrors errors )
        {
          
            var targetFile = new FileInfo(fileName);

            if (!targetFile.Exists)
            {

                errors.Add("导入的数据文件不存在");
                return false;
            }

            var excelFile = new ExcelQueryFactory(fileName);

            //对应列头
			 				 excelFile.AddMapping<MIS_Article_AlbumsModel>(x => x.ArticleId, "ArticleId");
				 excelFile.AddMapping<MIS_Article_AlbumsModel>(x => x.BigImg, "BigImg");
				 excelFile.AddMapping<MIS_Article_AlbumsModel>(x => x.SmallImg, "SmallImg");
				 excelFile.AddMapping<MIS_Article_AlbumsModel>(x => x.Remark, "Remark");
 
            //SheetName
            var excelContent = excelFile.Worksheet<MIS_Article_AlbumsModel>(0);
            int rowIndex = 1;
            //检查数据正确性
            foreach (var row in excelContent)
            {
                var errorMessage = new StringBuilder();
                var entity = new MIS_Article_AlbumsModel();
						 				  entity.Id = row.Id;
				  entity.ArticleId = row.ArticleId;
				  entity.BigImg = row.BigImg;
				  entity.SmallImg = row.SmallImg;
				  entity.Remark = row.Remark;
 
                //=============================================================================
                if (errorMessage.Length > 0)
                {
                    errors.Add(string.Format(
                        "第 {0} 列发现错误：{1}{2}",
                        rowIndex,
                        errorMessage,
                        "<br/>"));
                }
                list.Add(entity);
                rowIndex += 1;
            }
            if (errors.Count > 0)
            {
                return false;
            }
            return true;
        }

