		 /// <summary>
        /// 校验Excel数据,这个方法一般用于重写校验逻辑
        /// </summary>
        public virtual bool CheckImportData(string fileName, List<MIS_WebIM_MessageModel> list,ref ValidationErrors errors )
        {
          
            var targetFile = new FileInfo(fileName);

            if (!targetFile.Exists)
            {

                errors.Add("导入的数据文件不存在");
                return false;
            }

            var excelFile = new ExcelQueryFactory(fileName);

            //对应列头
			 				 excelFile.AddMapping<MIS_WebIM_MessageModel>(x => x.Message, "Message");
				 excelFile.AddMapping<MIS_WebIM_MessageModel>(x => x.Sender, "Sender");
				 excelFile.AddMapping<MIS_WebIM_MessageModel>(x => x.receiver, "receiver");
				 excelFile.AddMapping<MIS_WebIM_MessageModel>(x => x.State, "State");
				 excelFile.AddMapping<MIS_WebIM_MessageModel>(x => x.SendDt, "SendDt");
				 excelFile.AddMapping<MIS_WebIM_MessageModel>(x => x.receiverTitle, "receiverTitle");
 
            //SheetName
            var excelContent = excelFile.Worksheet<MIS_WebIM_MessageModel>(0);
            int rowIndex = 1;
            //检查数据正确性
            foreach (var row in excelContent)
            {
                var errorMessage = new StringBuilder();
                var entity = new MIS_WebIM_MessageModel();
						 				  entity.Id = row.Id;
				  entity.Message = row.Message;
				  entity.Sender = row.Sender;
				  entity.receiver = row.receiver;
				  entity.State = row.State;
				  entity.SendDt = row.SendDt;
				  entity.receiverTitle = row.receiverTitle;
 
                //=============================================================================
                if (errorMessage.Length > 0)
                {
                    errors.Add(string.Format(
                        "第 {0} 列发现错误：{1}{2}",
                        rowIndex,
                        errorMessage,
                        "<br/>"));
                }
                list.Add(entity);
                rowIndex += 1;
            }
            if (errors.Count > 0)
            {
                return false;
            }
            return true;
        }

