		 /// <summary>
        /// 校验Excel数据,这个方法一般用于重写校验逻辑
        /// </summary>
        public virtual bool CheckImportData(string fileName, List<Spl_WareUnitConvertModel> list,ref ValidationErrors errors )
        {
          
            var targetFile = new FileInfo(fileName);

            if (!targetFile.Exists)
            {

                errors.Add("导入的数据文件不存在");
                return false;
            }

            var excelFile = new ExcelQueryFactory(fileName);

            //对应列头
			 				 excelFile.AddMapping<Spl_WareUnitConvertModel>(x => x.Name, "Name");
				 excelFile.AddMapping<Spl_WareUnitConvertModel>(x => x.MainUnit, "MainUnit");
				 excelFile.AddMapping<Spl_WareUnitConvertModel>(x => x.AssistUnit, "AssistUnit");
				 excelFile.AddMapping<Spl_WareUnitConvertModel>(x => x.ConvertRate, "ConvertRate");
				 excelFile.AddMapping<Spl_WareUnitConvertModel>(x => x.CreateTime, "CreateTime");
 
            //SheetName
            var excelContent = excelFile.Worksheet<Spl_WareUnitConvertModel>(0);
            int rowIndex = 1;
            //检查数据正确性
            foreach (var row in excelContent)
            {
                var errorMessage = new StringBuilder();
                var entity = new Spl_WareUnitConvertModel();
						 				  entity.Id = row.Id;
				  entity.Name = row.Name;
				  entity.MainUnit = row.MainUnit;
				  entity.AssistUnit = row.AssistUnit;
				  entity.ConvertRate = row.ConvertRate;
				  entity.CreateTime = row.CreateTime;
 
                //=============================================================================
                if (errorMessage.Length > 0)
                {
                    errors.Add(string.Format(
                        "第 {0} 列发现错误：{1}{2}",
                        rowIndex,
                        errorMessage,
                        "<br/>"));
                }
                list.Add(entity);
                rowIndex += 1;
            }
            if (errors.Count > 0)
            {
                return false;
            }
            return true;
        }

