		 /// <summary>
        /// 校验Excel数据,这个方法一般用于重写校验逻辑
        /// </summary>
        public virtual bool CheckImportData(string fileName, List<Spl_WarehouseAllocationDetailsModel> list,ref ValidationErrors errors )
        {
          
            var targetFile = new FileInfo(fileName);

            if (!targetFile.Exists)
            {

                errors.Add("导入的数据文件不存在");
                return false;
            }

            var excelFile = new ExcelQueryFactory(fileName);

            //对应列头
			 				 excelFile.AddMapping<Spl_WarehouseAllocationDetailsModel>(x => x.WareDetailsId, "WareDetailsId");
				 excelFile.AddMapping<Spl_WarehouseAllocationDetailsModel>(x => x.WarehouseId, "WarehouseId");
				 excelFile.AddMapping<Spl_WarehouseAllocationDetailsModel>(x => x.WarehouseAllocationId, "WarehouseAllocationId");
				 excelFile.AddMapping<Spl_WarehouseAllocationDetailsModel>(x => x.Quantity, "Quantity");
				 excelFile.AddMapping<Spl_WarehouseAllocationDetailsModel>(x => x.Price, "Price");
				 excelFile.AddMapping<Spl_WarehouseAllocationDetailsModel>(x => x.TotalPrice, "TotalPrice");
				 excelFile.AddMapping<Spl_WarehouseAllocationDetailsModel>(x => x.Defined, "Defined");
				 excelFile.AddMapping<Spl_WarehouseAllocationDetailsModel>(x => x.CreateTime, "CreateTime");
 
            //SheetName
            var excelContent = excelFile.Worksheet<Spl_WarehouseAllocationDetailsModel>(0);
            int rowIndex = 1;
            //检查数据正确性
            foreach (var row in excelContent)
            {
                var errorMessage = new StringBuilder();
                var entity = new Spl_WarehouseAllocationDetailsModel();
						 				  entity.Id = row.Id;
				  entity.WareDetailsId = row.WareDetailsId;
				  entity.WarehouseId = row.WarehouseId;
				  entity.WarehouseAllocationId = row.WarehouseAllocationId;
				  entity.Quantity = row.Quantity;
				  entity.Price = row.Price;
				  entity.TotalPrice = row.TotalPrice;
				  entity.Defined = row.Defined;
				  entity.CreateTime = row.CreateTime;
 
                //=============================================================================
                if (errorMessage.Length > 0)
                {
                    errors.Add(string.Format(
                        "第 {0} 列发现错误：{1}{2}",
                        rowIndex,
                        errorMessage,
                        "<br/>"));
                }
                list.Add(entity);
                rowIndex += 1;
            }
            if (errors.Count > 0)
            {
                return false;
            }
            return true;
        }

