		 /// <summary>
        /// 校验Excel数据,这个方法一般用于重写校验逻辑
        /// </summary>
        public virtual bool CheckImportData(string fileName, List<SysAreasModel> list,ref ValidationErrors errors )
        {
          
            var targetFile = new FileInfo(fileName);

            if (!targetFile.Exists)
            {

                errors.Add("导入的数据文件不存在");
                return false;
            }

            var excelFile = new ExcelQueryFactory(fileName);

            //对应列头
			 				 excelFile.AddMapping<SysAreasModel>(x => x.Name, "Name");
				 excelFile.AddMapping<SysAreasModel>(x => x.ParentId, "ParentId");
				 excelFile.AddMapping<SysAreasModel>(x => x.Sort, "Sort");
				 excelFile.AddMapping<SysAreasModel>(x => x.Enable, "Enable");
				 excelFile.AddMapping<SysAreasModel>(x => x.CreateTime, "CreateTime");
				 excelFile.AddMapping<SysAreasModel>(x => x.IsMunicipality, "IsMunicipality");
				 excelFile.AddMapping<SysAreasModel>(x => x.IsHKMT, "IsHKMT");
				 excelFile.AddMapping<SysAreasModel>(x => x.IsOther, "IsOther");
 
            //SheetName
            var excelContent = excelFile.Worksheet<SysAreasModel>(0);
            int rowIndex = 1;
            //检查数据正确性
            foreach (var row in excelContent)
            {
                var errorMessage = new StringBuilder();
                var entity = new SysAreasModel();
						 				  entity.Id = row.Id;
				  entity.Name = row.Name;
				  entity.ParentId = row.ParentId;
				  entity.Sort = row.Sort;
				  entity.Enable = row.Enable;
				  entity.CreateTime = row.CreateTime;
				  entity.IsMunicipality = row.IsMunicipality;
				  entity.IsHKMT = row.IsHKMT;
				  entity.IsOther = row.IsOther;
 
                //=============================================================================
                if (errorMessage.Length > 0)
                {
                    errors.Add(string.Format(
                        "第 {0} 列发现错误：{1}{2}",
                        rowIndex,
                        errorMessage,
                        "<br/>"));
                }
                list.Add(entity);
                rowIndex += 1;
            }
            if (errors.Count > 0)
            {
                return false;
            }
            return true;
        }

