		 /// <summary>
        /// 校验Excel数据,这个方法一般用于重写校验逻辑
        /// </summary>
        public virtual bool CheckImportData(string fileName, List<SysUserConfigModel> list,ref ValidationErrors errors )
        {
          
            var targetFile = new FileInfo(fileName);

            if (!targetFile.Exists)
            {

                errors.Add("导入的数据文件不存在");
                return false;
            }

            var excelFile = new ExcelQueryFactory(fileName);

            //对应列头
			 				 excelFile.AddMapping<SysUserConfigModel>(x => x.Name, "Name");
				 excelFile.AddMapping<SysUserConfigModel>(x => x.Value, "Value");
				 excelFile.AddMapping<SysUserConfigModel>(x => x.Type, "Type");
				 excelFile.AddMapping<SysUserConfigModel>(x => x.State, "State");
				 excelFile.AddMapping<SysUserConfigModel>(x => x.UserId, "UserId");
 
            //SheetName
            var excelContent = excelFile.Worksheet<SysUserConfigModel>(0);
            int rowIndex = 1;
            //检查数据正确性
            foreach (var row in excelContent)
            {
                var errorMessage = new StringBuilder();
                var entity = new SysUserConfigModel();
						 				  entity.Id = row.Id;
				  entity.Name = row.Name;
				  entity.Value = row.Value;
				  entity.Type = row.Type;
				  entity.State = row.State;
				  entity.UserId = row.UserId;
 
                //=============================================================================
                if (errorMessage.Length > 0)
                {
                    errors.Add(string.Format(
                        "第 {0} 列发现错误：{1}{2}",
                        rowIndex,
                        errorMessage,
                        "<br/>"));
                }
                list.Add(entity);
                rowIndex += 1;
            }
            if (errors.Count > 0)
            {
                return false;
            }
            return true;
        }

